<?phpinclude_once('../models/startup.php');include_once('../models/news.php');startup();// Установка параметров, подключение к БД, запуск сессии.//Выбор статьиif (isset($_GET['id']))	{	$row = articles_get($_GET['id']);		include('../views/edit.php');	die();	}// Извлечение статей.$articles = articles_all();// Кодировка.header('Content-type: text/html; charset=utf-8');// Вывод в шаблон.$title = 'Список статей';// Внутренний шаблон.$content = view_include('../views/editor.php', array('articles' => $articles));// Внешний шаблон.$page = view_include(	'../views/index.php',	array('title' => $title, 'content' => $content));// Вывод.echo $page;