<?phpinclude_once('../models/startup.php');include_once('../models/news.php');// Установка параметров, подключение к БД, запуск сессии.startup();// Обработка отправки формы.if (!empty($_POST)){	if (articles_new($_POST['title'], $_POST['content']))	{		header('Location: editor.php');		die();	}		$title = $_POST['title'];	$content = $_POST['content'];	$error = true;}else{	$title = '';	$content = '';	$error = false;}// Кодировка.header('Content-type: text/html; charset=utf-8');// Вывод в шаблон.$title = 'Создание статьи';// Внутренний шаблон.$content = view_include('../views/new.php', array('articles' => $articles));// Внешний шаблон.$page = view_include(	'../views/index.php',	array('title' => $title, 'content' => $content));// Вывод.echo $page;